import React, { Component } from 'react'
// import store from '../Reducers/index';
import { connect } from "react-redux";

import {DisplayAll,DisplayActive, DisplayCompleted} from "../Actions/Action"

export class Filter extends Component {

  render() {
    return (
      <div className="container filterdiv">
        {/* {store.getState().todoitems.length} */}
        <span className="count float-left">   Item left </span>
        {/* <button className="filterbtn" onClick={()=>{store.dispatch(DisplayAll())}} > All </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayActive())}} > Active </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayCompleted())}}  > Completed </button> */}
         <button className="filterbtn" onClick={()=>{showAll}} > All </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayActive())}} > Active </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayCompleted())}}  > Completed </button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
	return{
		showAll: () => dispatch(DisplayAll),
	showActive : () => dispatch(DisplayActive),
	showCompleted: () => dispatch(DisplayCompleted)
	}
}
const FilterList = connect(null,mapDispatchToProps)(Filter);
export default FilterList

// export default Filter
