    // this.setState({
    //     caption: event.target.value,
    //     isCompleted : false
    // })
    console.log(event.target.value);
    store.dispatch(AddTodo);

    console.log(this.state.todoitems);
    console.log("Length of todoarray = " + this.state.todoitems.length );

}


export const keyPress = (e,key)=>{
    // console.log("e = " + e + " key = " + key.target.value);        
    this.state.todoitems[e].caption = key.target.value;
    this.setState({
        todoitems: this.state.todoitems
    })
}


export const removeItem = (e,key)=>{
       console.log("e = " + e + " Key = " + key);            
        this.state.todoitems.splice(e,1);
        this.setState({
           todoitems : this.state.todoitems
        })
}


