export const checkmate = (e,key)=>{
    var li = key.target.parentNode;

    this.toggle(e);
    if(this.state.todoitems[e].isCompleted === true){          
        li.children[1].style.textDecoration="line-through";
        li.children[1].disabled = true;
    }
    else{
        li.children[1].style.textDecoration="none";
        li.children[1].disabled = false;
    }   
}
export default submitFomHandler;
