import {  createStore} from "redux";

export const initState = {
    todoitems: [{
        caption: "Java",
        isCompleted: false
    }],
    filter: {
        all: true,
        active: false,
        completed: false
    }
}

const todoreducer = (state = initState, action) => {
    console.log("Action triggered : " + action.type);
    switch (action.type) {
        case "ADD-TODO":
              state.todoitems = [  ...state.todoitems ,  { caption: action.todo, isCompleted: action.isCompleted  } ]
             return state ;

        case "REMOVE-TODO":
            state.todoitems = state.todoitems.filter((_, i) => i !== action.index)
            return state ;

        case "TOGGLE-TODO":
            state.todoitems[action.index].isCompleted = !state.todoitems[action.index].isCompleted
            return state;

        case "EDIT-TODO":
        console.log(`index ==== ${action.index} Newvalue === ${action.newcaption}`)
            state.todoitems[action.index].caption = action.newcaption
            return state;

        case "ACTIVE":
        console.log("inside active");
            // state.filter.active = true;
            // state.filter.all = false;
            // state.filter.completed = false;
            // let active_todos = [];
            // for (let i = 0; i < state.todoitems.length; i++) {
            //     if (state.todoitems[i].isCompleted === false) {
            //         active_todos.push(state.todoitems[i]);
            //     }
            // }
            return {
                state
            }
        case "COMPLETED":
            let completed_todos = [];
            for (let i = 0; i < state.todoitems.length; i++) {
                if (state.todoitems[i].isCompleted === true) {
                    completed_todos.push(state.todoitems[i]);
                }
            }
            return {
                state
            }
        default:
            return state;
    }
}

const store = createStore(todoreducer);

export default store;
