import React, { Component } from 'react'
import store from "../Reducers/index";
// import submitFomHandler from "../Actions/Action";
import List from "./List";
import Form from "./Form";
import Filter from "./Filter";
import "../style.css";
import  { AddTodo , RemoveTodo} from "../Actions/Action";

class TodoApp extends Component {    
    componentDidMount() {
      store.subscribe(()=>{
        console.log("Store state : " + store.getState());
        this.forceUpdate();
      })
    }

  render() {

    let todo = store.getState().todoitems;
    let filter = store.getState().filter;
    let filteredState = { todo , filter }
    if(filter ===  "ALL"){
        filteredState.todo = todo
    }
    else if(filter === "ACTIVE"){
        filteredState.todo = todo.filter(item => !item.isCompleted )
        console.log(filteredState);
    }
    else if(filter === "COMPLETED"){
        filteredState.todo = todo.filter(item => item.isCompleted )
    }

    // return (
    //   <ToDoItem todo={filteredState} />
    // )

      console.log("Store : " + store);
    return (
      <div className="conatiner h-50">
        <h1 id="main-heading">todos</h1>
       <div className="container w-50">
            <Form/>
            <List todo={filteredState} />
       </div>

        <Filter/>

      </div>
    )
  }
}

export default TodoApp;


