import React, { Component } from 'react'
import store from '../Reducers/index';
import {DisplayAll,DisplayActive, DisplayCompleted} from "../Actions/Action"

export class Filter extends Component {

  render() {
    return (
      <div>
        <span className="count"> {store.getState().todoitems.length} </span>
        <button className="filterbtn btn btn-info" onClick={()=>{store.dispatch(DisplayAll())}} > All </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayActive())}} > Active </button>
        <button className="filterbtn" onClick={()=>{store.dispatch(DisplayCompleted())}}  > Completed </button>
      </div>
    )
  }
}

export default Filter
