import React, { Component } from 'react'
import store from "../Reducers/index";
import checkmate, { DeleteTodo } from "../Actions/Action";
import keyPress from "../Actions/Action";
import removeItem from "../Actions/Action";



// import initState from "../Reducers/index";

class List extends Component {
    render() {
        return (
            <ul>
            {store.getState().todoitems.map( (item,key) =>        
            <li key = {key} className="list-group-item"> 
            <input type="checkbox" className="float-left my-3" onClick={checkmate.bind(this,key)} />  
            <input type="text" value={item.caption} className="myinput" onChange={keyPress} />              
            <button className="btn btn-danger mybtn float-right" onClick ={removeItem.bind(this,key)} > Delete </button>               
            </li>)}
            </ul>
        );
    }
}


export default List;
