import React, { Component } from 'react'
import store from "../Reducers/index";
import checkmate, { DeleteTodo, ToggleTodo } from "../Actions/Action";
import keyPress from "../Actions/Action";
import removeItem from "../Actions/Action";




// import initState from "../Reducers/index";

class List extends Component {

    handleRemove = (index)=>{
        store.dispatch(DeleteTodo(index));
    }
    handleToggle = (e,index) => {
        store.dispatch(ToggleTodo(index));
        // console.log("Event  = " + e + "Index : "+ index);
    }

    render() {
        return (
            <ul className = "list-group">
            {store.getState().todoitems.map( (item,key) =>        
            <li key = {key} className="list-group-item"> 
            {item.isCompleted? <input type="checkbox" className="float-left my-3 strike" onClick={() => this.handleToggle(this,key)} /> : 
            <input type="checkbox" className="float-left my-3" onClick={() => this.handleToggle(this,key)} /> } 
            <input type="text" value={item.caption} className="myinput" onChange={keyPress} />              
            <button className="btn btn-danger mybtn float-right" onClick ={this.handleRemove.bind(this,key)} > Delete </button>               
            </li>)}
            </ul>
        );
    }
}


export default List;
